import classes from './Counter.module.css'
import { useSelector, useDispatch } from 'react-redux'
import store from '../store'
import React, { createStore } from 'react'
const Counter = () => {
  // const counter = useSelector((state) => state.counter)

  // const show = useSelector((state) => state.showCounter)

  // const dispatch = useDispatch()

  // const incrementHandler = () => {
  //   dispatch({ type: 'INC' })
  // }
  // const incrementByFiveHandler = () => {
  //   dispatch({ type: 'INC5'})
  // }

  // const decerementHandler = () => {
  //   dispatch({ type: 'DEC' })
  // }

  // const toggleCounterHandler = () => {
  //   dispatch({ type: 'TOGGLE' })
  // }

  var show = false,
    counter = 0

  const counterReducer = (state = { counter: 1, show: true }, action) => {
    switch (action.type) {
      case 'INC':
        return {
          counter: state.counter + 1,
        }
      default:
        return state
    }
  }

  const store = createStore(counterReducer)

  const subscriberFunction = () => {
    const latestState = store.getState()
    console.log('latestState', latestState)
    show = latestState.show
    counter = latestState.counter
  }

  store.subscribe(subscriberFunction)


  const incrementHandler = () => {
    store.dispatch({ type: 'INC' })
  }

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {show && <div className={classes.value}>Counter value : {counter}</div>}
      {!show && <div className={classes.value}>Counter hidden!</div>}
      <div>
        <button onClick={incrementHandler}>+</button>
        <button onClick={incrementByFiveHandler}>+5</button>
        <button onClick={decerementHandler}>-</button>
      </div>
      <button onClick={toggleCounterHandler}>{show ? 'Hide' : 'Show'}</button>
    </main>
  )
}

export default Counter
